"use client";
import { Button } from "@/components/nurui/future-button";
import { ShoppingCart } from "lucide-react";

export default function FutureButtonDemo() {
  return (
    <div className="flex justify-center">
      <Button variant="destructive">
        <ShoppingCart className="w-4 h-4 mr-2" />
        Purchase Item
      </Button>
    </div>
  );
}

import type React from "react";
import { Frame } from "@/components/nurui/future-frame";
import { twMerge } from "tailwind-merge";
import { cva, type VariantProps } from "class-variance-authority";

const buttonVariants = cva(
  [
    "group font-bold mb-2 relative px-8 py-2 cursor-pointer transition-all [&:hover_svg]:drop-shadow-xl outline-none",
    "[&>span]:relative [&>span]:flex [&>span]:items-center [&>span]:justify-center [&>span]:group-hover:text-shadow-lg",
  ],
  {
    variants: {
      variant: {
        default:
          "[--color-frame-1-stroke:theme(colors.blue.500)] [--color-frame-1-fill:theme(colors.blue.500/0.22)] [--color-frame-2-stroke:theme(colors.blue.500)] [--color-frame-2-fill:theme(colors.blue.500/0.1)] text-blue-100 [&:hover_svg]:drop-shadow-blue-500/50",
        accent:
          "[--color-frame-1-stroke:theme(colors.purple.500)] [--color-frame-1-fill:theme(colors.purple.500/0.4)] [--color-frame-2-stroke:theme(colors.purple.500)] [--color-frame-2-fill:theme(colors.purple.500/0.2)] text-purple-100 [&:hover_svg]:drop-shadow-purple-500/50",
        destructive:
          "[--color-frame-1-stroke:theme(colors.red.500)] [--color-frame-1-fill:theme(colors.red.500/0.22)] [--color-frame-2-stroke:theme(colors.red.500)] [--color-frame-2-fill:theme(colors.red.500/0.1)] text-red-100 [&:hover_svg]:drop-shadow-red-500/50",
        secondary:
          "[--color-frame-1-stroke:theme(colors.gray.500)] [--color-frame-1-fill:theme(colors.gray.500/0.15)] [--color-frame-2-stroke:theme(colors.gray.500)] [--color-frame-2-fill:theme(colors.gray.500/0.1)] text-gray-100 [&:hover_svg]:drop-shadow-gray-500/50",
        success:
          "[--color-frame-1-stroke:theme(colors.green.500)] [--color-frame-1-fill:theme(colors.green.500/0.22)] [--color-frame-2-stroke:theme(colors.green.500)] [--color-frame-2-fill:theme(colors.green.500/0.1)] text-green-100 [&:hover_svg]:drop-shadow-green-500/50",
      },
      shape: {
        default: "",
        flat: "[--color-frame-2-stroke:transparent] [--color-frame-2-fill:transparent]",
        simple: "ps-8 pe-6",
        "tab-left": "",
        "tab-center": "",
        "tab-right": "",
      },
    },
    defaultVariants: {
      variant: "default",
      shape: "default",
    },
  },
);

function Button({
  className,
  children,
  variant = "default",
  shape = "default",
  customPaths,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    customPaths?: string[];
  }) {
  return (
    <button
      {...props}
      className={twMerge(buttonVariants({ variant, shape, className }))}
    >
      <div className="absolute inset-0 -mb-2">
        {!customPaths && (shape == "default" || shape == "flat") && (
          <Frame
            paths={JSON.parse(
              '[{"show":true,"style":{"strokeWidth":"1","stroke":"var(--color-frame-1-stroke)","fill":"var(--color-frame-1-fill)"},"path":[["M","17","0"],["L","180","0"],["L","200","9.5"],["L","182","34"],["L","4","34"],["L","0","25"],["L","17","0"]]},{"show":true,"style":{"strokeWidth":"1","stroke":"var(--color-frame-2-stroke)","fill":"var(--color-frame-2-fill)"},"path":[["M","9","34"],["L","178","34"],["L","175","40"],["L","12","40"],["L","9","34"]]}]',
            )}
          />
        )}
        {!customPaths && shape == "simple" && (
          <Frame
            paths={JSON.parse(
              '[{"show":true,"style":{"strokeWidth":"1","stroke":"var(--color-frame-1-stroke)","fill":"var(--color-frame-1-fill)"},"path":[["M","17","0"],["L","200","0"],["L","200","34"],["L","3","34"],["L","0","24"],["L","17","0"]]},{"show":true,"style":{"strokeWidth":"1","stroke":"var(--color-frame-2-stroke)","fill":"var(--color-frame-2-fill)"},"path":[["M","8","34"],["L","195","34"],["L","193","40"],["L","10","40"],["L","8","34"]]}]',
            )}
          />
        )}
        {customPaths?.map((customPath, customPathKey) => {
          return <Frame key={customPathKey} paths={JSON.parse(customPath)} />;
        })}
      </div>
      <span>{children}</span>
    </button>
  );
}

export { Button };

"use client";

import type React from "react";

import { useRef, useEffect } from "react";
import { twMerge } from "tailwind-merge";
// import type { Paths } from "@/types/frame"

type PathCommand = ["M" | "L", string, string];

interface PathProps {
  show: boolean;
  style: {
    strokeWidth: string;
    stroke: string;
    fill: string;
  };
  path: PathCommand[];
}

type Paths = PathProps[];

function setupSvgRenderer({
  el,
  paths,
  enableBackdropBlur,
}: {
  el: SVGSVGElement;
  paths: Paths;
  enableBackdropBlur: boolean | undefined;
}) {
  // Clear previous content
  while (el.firstChild) {
    el.removeChild(el.firstChild);
  }

  // Optionally add backdrop blur filter
  if (enableBackdropBlur) {
    const defs = document.createElementNS("http://www.w3.org/2000/svg", "defs");
    const filter = document.createElementNS(
      "http://www.w3.org/2000/svg",
      "filter",
    );
    filter.setAttribute("id", "backdrop-blur");
    filter.innerHTML = `
      <feGaussianBlur stdDeviation="8" result="blur"/>
      <feMerge>
        <feMergeNode in="blur"/>
        <feMergeNode in="SourceGraphic"/>
      </feMerge>
    `;
    defs.appendChild(filter);
    el.appendChild(defs);
  }

  // Convert path commands to SVG path string
  const convertPathToString = (pathCommands: [string, string, string][]) => {
    return (
      pathCommands
        .map(([command, x, y]) => {
          // Replace percentage-based coordinates with actual values
          const processCoordinate = (coord: string, isX: boolean) => {
            if (coord.includes("%")) {
              // For demo purposes, we'll use viewport-relative values
              return coord
                .replace("100%", isX ? "200" : "40")
                .replace("0%", "0");
            }
            return coord;
          };

          const processedX = processCoordinate(x, true);
          const processedY = processCoordinate(y, false);

          return `${command}${processedX},${processedY}`;
        })
        .join(" ") + " Z"
    );
  };

  // Render each path
  paths.forEach((pathProps) => {
    if (!pathProps.show) return;

    const path = document.createElementNS("http://www.w3.org/2000/svg", "path");

    // Set the path data
    const pathString = convertPathToString(pathProps.path);
    path.setAttribute("d", pathString);

    // Apply styles
    Object.entries(pathProps.style).forEach(([key, value]) => {
      if (key === "strokeWidth") {
        path.setAttribute("stroke-width", value);
      } else {
        path.setAttribute(key, value);
      }
    });

    if (enableBackdropBlur) {
      path.setAttribute("filter", "url(#backdrop-blur)");
    }

    el.appendChild(path);
  });

  return {
    destroy() {
      while (el.firstChild) {
        el.removeChild(el.firstChild);
      }
    },
  };
}

function Frame({
  className,
  paths,
  enableBackdropBlur,
  ...props
}: {
  paths: Paths;
  enableBackdropBlur?: boolean;
} & React.ComponentProps<"svg">) {
  const svgRef = useRef<SVGSVGElement | null>(null);

  useEffect(() => {
    if (svgRef.current && svgRef.current.parentElement) {
      const instance = setupSvgRenderer({
        el: svgRef.current,
        paths,
        enableBackdropBlur,
      });

      return () => instance.destroy();
    }
  }, [paths, enableBackdropBlur]);

  return (
    <svg
      {...props}
      className={twMerge(["absolute inset-0 size-full", className])}
      xmlns="http://www.w3.org/2000/svg"
      ref={svgRef}
    />
  );
}

export { Frame };
