"use client";
import { cn } from "@/lib/utils";
import * as React from "react";
import { StoryButton, ButtonProps } from "@/components/nurui/story-button";
import { PauseIcon, PlayIcon, ReplyIcon } from "lucide-react";

interface StoryProps extends React.HTMLAttributes<HTMLDivElement> {
  mediaLength: number;
  duration?: number;
}
interface StoryContextValue {
  mediaLength: number;
  currentIndex: number;
  progress: number;
  isPaused: boolean;
  isEnded: boolean;
  handleControl: () => void;
  setCurrentIndex: (index: number) => void;
  setIsPaused: (paused: boolean) => void;
  setIsEnded: (ended: boolean) => void;
}
const StoryContext = React.createContext<StoryContextValue | undefined>(
  undefined,
);
function useStoryContext() {
  const context = React.useContext(StoryContext);
  if (context === undefined) {
    throw new Error("useStoryContext must be used within a StoryProvider");
  }
  return context;
}
export const Story = React.forwardRef<HTMLDivElement, StoryProps>(
  ({ mediaLength, duration = 2000, className, children, ...props }, ref) => {
    const [currentIndex, setCurrentIndex] = React.useState(0);
    const [progress, setProgress] = React.useState(0);
    const [isPaused, setIsPaused] = React.useState(false);
    const [isEnded, setIsEnded] = React.useState(false);
    const progressRef = React.useRef<number>(0);
    const intervalRef = React.useRef<ReturnType<typeof setInterval> | null>(
      null,
    );

    React.useEffect(() => {
      progressRef.current = 0;
      setProgress(0);
    }, [currentIndex, duration, mediaLength]);
    React.useEffect(() => {
      if (mediaLength === 0 || isPaused) return;

      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }

      const tick = 50;
      const totalTicks = duration / tick;

      intervalRef.current = setInterval(() => {
        progressRef.current += 1;
        const newProgress = (progressRef.current / totalTicks) * 100;
        setProgress(newProgress);

        if (progressRef.current >= totalTicks) {
          clearInterval(intervalRef.current!);
          intervalRef.current = null;

          if (currentIndex < mediaLength - 1) {
            setCurrentIndex((idx) => idx + 1);
          } else {
            setIsPaused(true);
            setIsEnded(true);
          }
        }
      }, tick);

      return () => {
        if (intervalRef.current) {
          clearInterval(intervalRef.current);
          intervalRef.current = null;
        }
      };
    }, [isPaused, currentIndex, duration, mediaLength]);

    if (mediaLength === 0) {
      return (
        <div className="text-center text-secondary">No stories to display</div>
      );
    }

    const handleControl = () => {
      if (isEnded) {
        setCurrentIndex(0);
        setIsEnded(false);
        setIsPaused(false);
      } else {
        setIsPaused((prev) => !prev);
      }
    };

    return (
      <StoryContext.Provider
        value={{
          mediaLength,
          currentIndex,
          progress,
          isPaused,
          isEnded,
          handleControl,
          setCurrentIndex,
          setIsPaused,
          setIsEnded,
        }}
      >
        <div className={cn("mx-auto", className)} ref={ref} {...props}>
          {children}
        </div>
      </StoryContext.Provider>
    );
  },
);
Story.displayName = "Story";

export const StoryProgress = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & {
    progressWrapClass?: string;
    progressActiveClass?: string;
  }
>(({ className, progressWrapClass, progressActiveClass, ...props }, ref) => {
  const {
    mediaLength,
    currentIndex,
    progress,
    setCurrentIndex,
    setIsEnded,
    setIsPaused,
  } = useStoryContext();

  const handleProgressClick = (index: number) => {
    setCurrentIndex(index);
    setIsPaused(false);
    setIsEnded(false);
  };

  return (
    <div className={cn("space-x-1 flex", className)} ref={ref} {...props}>
      {Array.from({ length: mediaLength }).map((_, index) => {
        const isActive = index === currentIndex;
        const isCompleted = index < currentIndex;

        return (
          <div
            key={index}
            className={cn(
              "h-1 flex-1 rounded bg-secondary cursor-pointer transition-colors",
              "hover:bg-secondary/80",
              progressWrapClass,
            )}
            onClick={() => handleProgressClick(index)}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => {
              if (e.key === "Enter" || e.key === " ") {
                handleProgressClick(index);
              }
            }}
          >
            <div
              className={cn(
                "h-full rounded-[inherit] transition-all duration-200",
                isActive
                  ? "bg-primary"
                  : isCompleted
                    ? "bg-primary"
                    : "bg-transparent",
                progressActiveClass,
              )}
              style={{
                width: isActive ? `${progress}%` : isCompleted ? "100%" : "0%",
              }}
            />
          </div>
        );
      })}
    </div>
  );
});
StoryProgress.displayName = "StoryProgress";

export const StorySlide = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & { index: number }
>(({ index, className, ...props }, ref) => {
  const { currentIndex } = useStoryContext();
  if (index !== currentIndex) return null;
  return (
    <div className={cn("animate-in fade-in", className)} ref={ref} {...props} />
  );
});
StorySlide.displayName = "StorySlide";

export const StoryControls = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, ...props }, ref) => {
    const { isPaused, isEnded, handleControl } = useStoryContext();
    return (
      <StoryButton
        onClick={handleControl}
        size="icon"
        {...props}
        ref={ref}
        className={className}
      >
        {isPaused ? isEnded ? <ReplyIcon /> : <PlayIcon /> : <PauseIcon />}
      </StoryButton>
    );
  },
);
StoryControls.displayName = "StoryControls";

export const StoryOverlay: React.FC = () => (
  <div className=" absolute inset-0 ">
    <div className="absolute inset-x-0 top-0 h-24 bg-gradient-to-b from-black to-transparent" />
    <div className="absolute inset-x-0 bottom-0 h-40 bg-gradient-to-t from-black to-transparent" />
  </div>
);

import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";
import * as React from "react";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap rounded-lg text-sm font-medium transition-colors outline-offset-2 focus-visible:outline focus-visible:outline-2 focus-visible:outline-ring/70 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground shadow-sm shadow-black/5 hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm shadow-black/5 hover:bg-destructive/90",
        outline:
          "border border-input bg-background shadow-sm shadow-black/5 hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm shadow-black/5 hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-lg px-3 text-xs",
        lg: "h-10 rounded-lg px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const StoryButton = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button";
    return (
      <Comp className={cn(buttonVariants({ variant, size, className }))} ref={ref} {...props} />
    );
  },
);
StoryButton.displayName = "StoryButton";

export { StoryButton, buttonVariants };

"use client";

import * as DialogPrimitive from "@radix-ui/react-dialog";
import * as React from "react";

import { cn } from "@/lib/utils";
import { Cross2Icon } from "@radix-ui/react-icons";

const Dialog = DialogPrimitive.Root;

const DialogTrigger = DialogPrimitive.Trigger;

const DialogPortal = DialogPrimitive.Portal;

const DialogClose = DialogPrimitive.Close;

const DialogOverlay = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Overlay
    ref={ref}
    className={cn(
      "fixed inset-0 z-[101] bg-black/80 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className,
    )}
    {...props}
  />
));
DialogOverlay.displayName = DialogPrimitive.Overlay.displayName;

const DialogContent = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Content>
>(({ className, children, ...props }, ref) => (
  <DialogPortal>
    <DialogOverlay />
    <DialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-1/2 top-1/2 z-[101] grid max-h-[calc(100%-4rem)] w-full -translate-x-1/2 -translate-y-1/2 gap-4 overflow-y-auto border bg-background p-6 shadow-lg shadow-black/5 duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:max-w-[400px] sm:rounded-xl",
        className,
      )}
      {...props}
    >
      {children}
      <DialogPrimitive.Close className="group absolute right-3 top-3 flex size-7 items-center justify-center rounded-lg outline-offset-2 transition-colors focus-visible:outline focus-visible:outline-2 focus-visible:outline-ring/70 disabled:pointer-events-none">
        <Cross2Icon
          width={16}
          height={16}
          strokeWidth={2}
          className="opacity-60 transition-opacity group-hover:opacity-100"
        />
        <span className="sr-only">Close</span>
      </DialogPrimitive.Close>
    </DialogPrimitive.Content>
  </DialogPortal>
));
DialogContent.displayName = DialogPrimitive.Content.displayName;

const DialogHeader = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div className={cn("flex flex-col space-y-1.5 text-center sm:text-left", className)} {...props} />
);
DialogHeader.displayName = "DialogHeader";

const DialogFooter = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn("flex flex-col-reverse gap-2 sm:flex-row sm:justify-end sm:gap-3", className)}
    {...props}
  />
);
DialogFooter.displayName = "DialogFooter";

const DialogTitle = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Title
    ref={ref}
    className={cn("text-lg font-semibold tracking-tight", className)}
    {...props}
  />
));
DialogTitle.displayName = DialogPrimitive.Title.displayName;

const DialogDescription = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
));
DialogDescription.displayName = DialogPrimitive.Description.displayName;

export {
  Dialog,
  DialogClose,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogOverlay,
  DialogPortal,
  DialogTitle,
  DialogTrigger,
};

